import React, {useState} from "react"

const TrueFalseQuestion = ({question}) => {
    const [seletedChoice, setSelectedChoice] = useState([])

    return (
        <>
            <h3>{question.title}</h3>
            <div>
                <label><input type="radio" name={question._id} value="true"/>True</label>
            </div>
            <div>
                <label><input type="radio" name={question._id} value="false"/>False</label>
            </div>
        </>
    )
}

export default TrueFalseQuestion
