import React,{useState} from "react";
import {Link, Route} from "react-router-dom";
import courseService from "../services/course-sevice"
import CourseTable from "./course-table";
import CourseGrid from "./course-grid";

export default class CourseManager
    extends React.Component {
    state = {
        courses: [],
        newTitle: ''
    }

    componentDidMount() {
        courseService.findAllCourses()
            .then(course => this.setState({course}))
    }

    updateCourse = (course) => {
        courseService.updateCourse(course._id, course)
            .then(status => {
                this.setState(prevState => {
                    let nextState = {...prevState}
                    nextState.course = prevState.course.map(c => {
                        if(c._id === course._id) {
                            return course
                        } else {
                            return c
                        }
                    })
                    return nextState
                })
            })
    }

    deleteCourse = (course) => {
        courseService.deleteCourse(course._id)
            .then(status => {
                this.setState(prevState => {
                    courses: prevState.course.filter(c => c._id !== course._id)
                })
            })
    }

    addCourse = (title) => {
        const newCourse = {
            title: title,
            owner: "me",
            lastModified: new Date().toISOString().split('T')[0]
        }
        courseService.createCourse(newCourse)
            .then(actualCourse => {
                this.state.courses.push(actualCourse)
                this.setState(this.state)
            })
    }

    updateInput = (e) => {
        this.setState({value: e.target.value})
    }

    render() {
        return(
            <div className="container">
                <div className="row wbdv-sticky-top wbdv-padding-5px">
                    <div className="col-1">
                        <i className="fa fa-bars fa-2x"></i>
                    </div>
                    <div className="col-3 d-none d-lg-block">
                        <h4>Course Manager</h4>
                    </div>
                    <div className="col-7">
                        <input className="form-control"
                               placeholder="New Course Title"
                               defaultValue="New Course"
                               />
                    </div>
                    <div className="col-1">
                        {/*<i className="fa fa-plus-circle fa-2x" ></i>*/}
                        <button onClick={this.addCourse}>
                            Add Course
                        </button>
                    </div>
                </div>
                <Route path="/courses/table" exact={true} >
                    <CourseTable
                        updateCourse={this.updateCourse}
                        deleteCourse={this.deleteCourse}
                        courses={this.state.courses}/>
                </Route>

                <Route path="/courses/grid" exact={true} >
                    <CourseGrid courses={this.state.courses}/>
                </Route>
            </div>




        )
    }
}
